import React, { useState, useEffect, ChangeEvent } from "react";
import CampService from "../network/camp-api";
import { Link } from "react-router-dom";
import ICampData from "../network/types/Camp";

const CampList: React.FC = () => {
    const [camp, setCamp] = useState<Array<ICampData>>([]);
    const [currentCamp, setCurrentCamp] = useState<ICampData | null>(null);
    const [currentIndex, setCurrentIndex] = useState<number>(-1);

    useEffect(() => {
        retrieveTutorials();
    }, []);


    const retrieveTutorials = () => {
        CampService.getAll()
            .then(response => {
                setCamp(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    const refreshList = () => {
        retrieveTutorials();
        setCurrentCamp(null);
        setCurrentIndex(-1);
    };

    const setActiveTutorial = (tutorial: ICampData, index: number) => {
        setCurrentCamp(tutorial);
        setCurrentIndex(index);
    };

    const removeAllTutorials = () => {
        CampService.removeAll()
            .then(response => {
                console.log(response.data);
                refreshList();
            })
            .catch(e => {
                console.log(e);
            });
    };

    return (
        <div className="list row">
            <div className="col-md-6">
                <h4>Camps List</h4>

                <ul className="list-group">
                    {camp &&
                    camp.map((camp, index) => (
                        <li
                            className={
                                "list-group-item " + (index === currentIndex ? "active" : "")
                            }
                            onClick={() => setActiveTutorial(camp, index)}
                            key={index}
                        >
                            {camp.campId}
                        </li>
                    ))}
                </ul>

                <button
                    className="m-3 btn btn-sm btn-danger"
                    onClick={removeAllTutorials}
                >
                    Remove All
                </button>
            </div>
            <div className="col-md-6">
                {currentCamp ? (
                    <div>
                        <h4>Tutorial</h4>
                        <div>
                            <label>
                                <strong>Title:</strong>
                            </label>{" "}
                            {currentCamp.campId}
                        </div>
                        <div>
                            <label>
                                <strong>Description:</strong>
                            </label>{" "}
                            {currentCamp.price}
                        </div>


                        <Link
                            to={"/camp/?campId=" + currentCamp.campId}
                            className="badge badge-warning"
                        >
                            Edit
                        </Link>
                    </div>
                ) : (
                    <div>
                        <br />
                        <p>Please click on a Tutorial...</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default CampList;
